mapper.update:
update members
	set email = CONCAT('deleted_user_', REPLACE(UUID(), '-', '')), deleted = 1
	where email = #{email}

UPDATE members
SET password = #{newPassword}
WHERE email = #{email}

mapper.select:
SELECT file_id
FROM member_file
WHERE member_id = #{memberId}

mapper.update:
UPDATE members
SET deleted = 1
WHERE member_id = #{memberId}

mapper.select:
SELECT f.file_path
FROM files f JOIN member_file mf ON f.file_id = mf.file_id
WHERE mf.member_id = #{memberId}
