mapper.select:
select member_id, id, password, name, email
	from members
	where id = #{id}

mapper.update:
update members
	set password = #{password}, name = #{name}, email = #{email}
	where id = #{id}

update members
	set id = CONCAT('deleted_user_', REPLACE(UUID(), '-', '')), deleted = 1
	where id = #{id}
